.card {
  font-family: arial;
  font-size: 20px;
  text-align: center;
  color: black;
  background-color: white;
}

.cloze {
  font-weight: bold;
  color: blue;
}

.card {
  font-family: arial;
  font-size: 20px;
  text-align: left;
  color: black;
  background-color: white;
  padding: 20px;
}
.question { font-weight: bold; margin-bottom: 15px; }
.option { margin: 5px 0; }
.correct { color: green; font-weight: bold; }
.incorrect { color: #888; }
.label { margin-top: 15px; font-style: italic; color: #555; font-size: 16px; }
